import React, { useState, useEffect } from "react";
import Employee from "../components/Employee";
import "./Search.css";
import axios from "axios";

const Search = props => {
  const [searchInput, setSearchInput] = useState("");
  const [matchList, setMatchList] = useState(props.data);
  console.log(matchList);
  const employees = props.data;
  console.log(employees);

  useEffect(() => {
    console.log("didmounT");
    setMatchList(props.refreshData());
  }, [matchList]);

  // const getData = () => {
  //   axios
  //     .get("/api/")
  //     .then(response => {
  //       console.log("data is taken");
  //       setMatchList(response.data);
  //     })
  //     .catch(err => {
  //       console.log(err);
  //     });
  // };

  // useEffect(() => {
  //   getData();
  //   console.log("data taken");
  // }, []);

  const handleChange = event => {
    // change state of search input based on input searchbar
    setSearchInput(event.target.value);
    console.log("search term:", searchInput);
  };

  /* match between employees properties and searchterm is being checked each time searchterm changes */
  useEffect(() => {
    console.log("didmount searchinput");
    let match = [];
    // iterate over employees
    employees.forEach(employee => {
      // if properties include searchinput
      if (
        employee.name.toLowerCase().includes(searchInput.toLowerCase()) ||
        employee.age.toString().includes(searchInput) ||
        (employee.gender.includes(searchInput) &&
          employee.gender.indexOf(searchInput) === 0) ||
        employee.eyeColor.includes(searchInput.toLowerCase()) ||
        employee.company.toLowerCase().includes(searchInput.toLowerCase()) ||
        employee.email.includes(searchInput.toLowerCase()) ||
        employee.phone
          .replace(/[+()-\s]/g, "")
          .includes(searchInput.replace(/[+()-\s]/g, "")) ||
        employee.address
          .replace(/\s/g, "")
          .includes(searchInput.replace(/\s/g, ""))
      ) {
        // update the state
        match.push(employee);
        setMatchList(match);
        console.log("match", match);
      }
      // otherwise update the state without them
      setMatchList(match);
    });
  }, [searchInput]);

  useEffect(() => {
    console.log("matchList updated");
  }, [matchList]);

  return (
    <div className="search">
      <form className="input-searchbar">
        <label htmlFor="search">Search: </label>
        <input
          className="input-field"
          type="text"
          name="search"
          value={searchInput}
          onChange={handleChange}
        />
      </form>
      <Employee data={matchList} />
    </div>
  );
};

export default Search;
