import React, { useState, useEffect } from "react";
import axios from "axios";
import Employee from "../components/Employee";

const Search = props => {
  const [searchInput, setSearchInput] = useState("");
  // const [employees, setEmployees] = useState([]);
  const [matchList, setMatchList] = useState([]);

  const employees = props.data;

  const handleChange = event => {
    // change state of search input based on input searchbar
    setSearchInput(event.target.value);
    console.log("search term:", searchInput);

    // create empty list for matches with searchterm
    // let matchList = [];
  };

  useEffect(() => {
    console.log("didmounT");
  }, []);

  useEffect(() => {
    console.log("didmount searchinput");

    /* check if searchterm matches the dataset properties */
    // iterate over employees
    employees.forEach(employee => {
      // if properties include searchinput
      if (
        employee.name.toLowerCase().includes(searchInput.toLowerCase()) ||
        employee.eyeColor.includes(searchInput.toLowerCase()) ||
        employee.company.toLowerCase().includes(searchInput.toLowerCase()) ||
        employee.gender.includes(searchInput.toLowerCase()) ||
        employee.email.includes(searchInput.toLowerCase()) ||
        employee.address.includes(searchInput.toLowerCase()) ||
        employee.phone
          .replace(/\s/g, "")
          .includes(searchInput.replace(/\s/g, ""))
      ) {
        // console.log(employee);

        // add to match-list
        // matchList.push(employee);
        // if (employee !=)
        setMatchList([...matchList, employee]);
        for (let i = 0; i < matchList.length; i++) {
          console.log(matchList[i]);
        }
        console.log("matchlist:", matchList[0]);
        console.log(typeof matchList);
        // display employee
      }
    });
    // setMatchList([]);
  }, [searchInput]);

  return (
    <div>
      <h1>Searchbar</h1>
      <form className="input-searchbar">
        <label htmlFor="search">Search</label>
        <input
          className="input-field"
          type="text"
          name="search"
          value={searchInput}
          onChange={handleChange}
        />
      </form>
      {/* <ul> */}
      {/* {matchList.forEach(employee => {
          return (
            <div key={employee._id}>
              <h3>{employee.name}</h3>
            </div>
          );
        })} */}
      {/* make a tenary here: if matchlist then show employee*/}
      <Employee data={matchList} />
    </div>
  );
};

export default Search;
